using System;namespace CSharp1Exercises.ControlFlow{    public class Conditionals    {
        /// <summary>        /// Write a program and ask the user to enter a number. The number should be between 1 to 10. If the user enters         /// a valid number, display "Valid" on the console. Otherwise, display "Invalid". (This logic is used a lot in         /// applications where values entered into input boxes need to be validated.)        /// </summary>        static void Main(string[] args)        {            Console.Write("Enter the first number: ");            var input_1 = Console.ReadLine();            var number_1 = Convert.ToInt32(input_1);            Console.Write("Enter the second number: ");            var input_2 = Console.ReadLine();            var number_2 = Convert.ToInt32(input_2);            var max = Math.Max(number_1, number_2);            Console.WriteLine("The bigger integer is: {0}", max);        }    }}